import React from 'react';
import './WidgetBar.css';

class PriceFilter extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			title: 'price filter',
			min: 0,
			max: 1000
		}
		this.changePriceRange = this.changePriceRange.bind(this);
	}
	changePriceRange(){
		console.log(this.$refs.sliderInput);
	}
	render(){
		return (
			<div className="widget-wrapper">
				<h3 className="widget-header">{this.state.title}</h3>
				<input type="range" ref="sliderInput" onChange={this.changePriceRange}/>
			</div>
		)
	}
}

export default PriceFilter;
