import React from 'react';
import Slider from 'rc-slider';
import Tooltip from 'rc-tooltip';
import 'rc-slider/assets/index.css';
import 'rc-tooltip/assets/bootstrap.css';
import './WidgetBar.css';

const createSliderWithTooltip = Slider.createSliderWithTooltip;
const Range = createSliderWithTooltip(Slider.Range);
const Handle = Slider.Handle;

function log(value) {
  console.log(value);
}

class PriceFilter extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			title: 'price filter',
			min: 250,
			max: 1000
		}
		// this.changePriceRange = this.changePriceRange.bind(this);
	}
	// changePriceRange(value){
	// 	this.setState({
	// 		min: 
	// 	})
	// }
	render(){
		return (
			<div className="widget-wrapper">
				<h3 className="widget-header">{this.state.title}</h3>
				<Tooltip
		      prefixCls="rc-slider-tooltip"
		      overlay={value}
		      visible={dragging}
		      placement="top"
		      key={index}>
		      <Handle value={value} />
		    </Tooltip>
				<Range min={0} max={1250} allowCross={false} 
							defaultValue={[this.state.min, this.state.max]}
							tipFormatter={value => `${value}$`} 
							onChange={log} />
				<label for id="minPrice">From</label>
				<input type="text" id="minPrice" value={log()}/>
				<label for id="maxPrice">To</label>
				<input type="text" id="maxPrice" />
			</div>
		)
	}
}

export default PriceFilter;
