import React from 'react';
import './LoadMore.css';

class LoadMore extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			loading: false
		};
	}
	render(){
		return (
			<div className="loadmore-box">
				{
					this.state.loading ?
						<button> 
							<span className="loadmore-box-dot"></span>
							<span className="loadmore-box-dot"></span>
							<span className="loadmore-box-dot"></span>
						</button> : 
						'Load More'
				}
			</div>
		)
	}
}

export default LoadMore;
