import React from 'react';
import './LoadMore.css';

class LoadMore extends React.Component{
	constructor(props){
		super(props);
		state = {
			loading: false
		};
	}
	render(){
		return (
			<div className="loadmore-box">
				{
					this.state.loading ? 'OK' : 'Hello'
				}
				<span className="loadmore-box-dot"></span>
				<span className="loadmore-box-dot"></span>
				<span className="loadmore-box-dot"></span>
			</div>
		)
	}
}

export default LoadMore;
