import React from 'react';
import './LoadMore.css';

class LoadMore extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			loading: false
		};
		this.handleLoadMore = this.handleLoadMore.bind(this);
	}
	handleLoadMore(){
		console.log('API Request!');
		this.setState({
			loading: true
		});
