		}, 3000);
	}
	render(){
		return (
			<div className="loadmore-box" onClick={this.handleLoadMore}>
				{
					this.state.loading ?
						<div> 
							<span className="loadmore-box-dot"></span>
							<span className="loadmore-box-dot"></span>
							<span className="loadmore-box-dot"></span>
						</div> : 
						<div className="loadmore-box-text">Load More</div>
				}
			</div>
		)
	}
}

export default LoadMore;
